import React from "react";

export const Menu = ({ title, image, ingredients, steps, time }) => {
  return (
    <div className="bg-pink-400 rounded-xl shadow-lg p-6">
      <h2 className="text-3xl font-bold mb-4">{title}</h2>
      <div className="flex justify-between items-center">
        <img src={image} alt={title} className="w-1/3 h-auto rounded-lg shadow-md" />
        <div className="w-2/3 ml-4">
          <h3 className="text-lg font-semibold mb-2">Ingredients</h3>
          <ul className="list-disc ml-4">
            {ingredients.map((ingredient, index) => (
              <li key={index}>{ingredient}</li>
            ))}
          </ul>
          <h3 className="text-lg font-semibold mb-2 mt-4">Steps</h3>
          <ol className="list-decimal ml-4">
            {steps.map((step, index) => (
              <li key={index}>{step}</li>
            ))}
          </ol>
        </div>
      </div>
      <div className="mt-4">
        <p className="text-sm font-medium">{time} minutes</p>
      </div>
    </div>
  );
};
