import React from 'react';

const Menu = () => {
  const menuItems = [
    { id: 1, name: 'Hamburguesa', description: 'Carne de res, queso cheddar, lechuga, tomate y salsa especial', price: 8.99 },
    { id: 2, name: 'Pizza', description: 'Pepperoni, queso mozzarella, salsa de tomate y orégano', price: 10.99 },
    { id: 3, name: 'Ensalada', description: 'Lechuga, tomate, pepino, cebolla y aderezo de la casa', price: 6.99 },
    { id: 4, name: 'Sándwich de pollo', description: 'Pollo a la parrilla, queso provolone, lechuga, tomate y mayonesa', price: 7.99 },
  ];

  return (
    <div className="bg-gray-100 py-8">
      <div className="max-w-screen-xl mx-auto px-4 sm:px-6 lg:px-8">
        <h2 className="text-3xl leading-9 font-bold tracking-tight text-gray-900 sm:text-4xl sm:leading-10 mb-4">
          Nuestro menú
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {menuItems.map(item => (
            <div key={item.id} className="rounded-lg shadow-lg overflow-hidden bg-white">
              <div className="relative pt-2 pb-2 px-4 sm:px-6">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-medium leading-6 text-gray-900">
                    {item.name}
                  </h3>
                  <span className="text-gray-600 font-medium">${item.price.toFixed(2)}</span>
                </div>
                <p className="mt-1 text-sm leading-5 text-gray-500">
                  {item.description}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Menu;
